import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import axios from 'axios';

class home extends Component {
    state = {
        screams: null
    }
    componentDidMount(){
        axios.get('/screams')
            .then(res => {
                console.log(res.data)//log data that is recieved
                this.setState({
                    screams: res.data
                })
            })
            .catch(err => console.log(err));
    }
    render() {
        //create func to show recentPosts
        let recentPosts = this.state.screams ? (
            this.state.screams.map((scream) => <p>{scream.body}</p>)
        //else
        ) : (
            <p>Loading..</p>
        );
        return (
            <Grid container spacing={16}>
                <Grid item sm={8} xs={12}>
                    {recentPosts} {/* Content  */}
                </Grid>
                <Grid item sm={4} xs={12}>
                    <p>Profile...</p>
                </Grid>

            </Grid>
        );
    }
}

export default home;
