import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Icon from '../Images/no-img.png';
import axios from 'axios';
import { Link } from 'react-router-dom';
import themeObject from '../Util/theme';

//MUI Stuff
import withStyles from '@material-ui/core/styles/withStyles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";
import CircularProgress from '@material-ui/core/CircularProgress';

//Redux


//import theme class from app.js page
const styles = {
    form: themeObject.form,
    logoImg: themeObject.image,
    pageTitle: themeObject.pageTitle,
    textField: themeObject.textField,
    button: themeObject.button,
    customError: themeObject.customError,
    progress: themeObject.progress
};

class signup extends Component {
    //controled component
    constructor() {
        super();
        this.state = {
            email: '',
            password: '',
            confirmPassword: '',
            userName: '',
            loading: false,
            errors: {}
        };
    }

    //func to handle once user clicks submit
    handleSubmit = (event) => {
        //prevent user from submitting empty values
        event.preventDefault();
        //while everything is processing show loading
        this.setState({
            loading: true
        });

        //Get data before submitting
        const newUserData = {
            email: this.state.email,
            password: this.state.password,
            confirmPassword: this.state.confirmPassword,
            userName: this.state.userName
        }

        axios.post('/signup', newUserData)
            .then(res => {
                //if successful
                console.log(res.data);
                //store token locally
                localStorage.setItem('FBIdToken', `Bearer ${res.data.token}`);
                //set loading screen to false
                this.setState({
                    loading: false
                });
                //send users to homepage
                this.props.history.push('/');
            })
            .catch(err => {
                //if errors, log error and set loading to false
                this.setState({
                    errors: err.response.data,
                    loading: false
                });
            });
    };

    //func to handleChnage in Textfields and get user input
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };


    render() {
        //import classes
        const { classes } = this.props;
        const { errors, loading } = this.state;

        return (
            <Grid container className={classes.form}>
                <Grid item sm/>
                <Grid item sm>
                    <img src={Icon} alt="NewUser" className={classes.logoImg}/>
                    <Typography variant="h2" className={classes.pageTitle}>Signup</Typography>
                    <form noValidate onSubmit={this.handleSubmit} >
                        <TextField
                            id="userName"
                            name="userName"
                            type="text"
                            label="Username"
                            className={classes.textField}
                            helperText={errors.userName}
                            error={errors.userName ? true : false}
                            value={this.state.userName}
                            onChange={this.handleChange}
                            fullWidth
                        />
                        <TextField 
                            id="email"
                            name="email"
                            type="email"
                            label="Email"
                            className={classes.textField}
                            helperText={errors.email}
                            error={errors.email ? true : false}
                            value={this.state.email}
                            onChange={this.handleChange}
                            fullWidth 
                        />
                        <TextField 
                            id="password" 
                            name="password" 
                            type="password" 
                            label="Password" 
                            className={classes.textField}
                            helperText={errors.password}
                            error={errors.password ? true : false}
                            value={this.state.password} 
                            onChange={this.handleChange} 
                            fullWidth 
                        />
                        <TextField
                            id="confirmPassword"
                            name="confirmPassword"
                            type="password"
                            label="Confirm Password"
                            className={classes.textField}
                            helperText={errors.confirmPassword}
                            error={errors.confirmPassword ? true : false}
                            value={this.state.confirmPassword}
                            onChange={this.handleChange}
                            fullWidth
                        />
                        {errors.general && (
                            <Typography variant="h2" className={classes.customError}>
                                {errors.general}
                            </Typography>
                        )}
                        <Button 
                            type="submit" 
                            variant="contained" 
                            color="primary" 
                            className={classes.button}
                            //Disable button when loading
                            disabled={loading}
                        >
                            Sign Up
                            {/* Will show a loading circle after user clicks login btn */}
                            {loading && (
                                <CircularProgress size={30} className={classes.progress}/>
                            )}
                        </Button>
                            {/* Text that will redirect user to creat a new account(signup) */}
                            <br/><br/><small>Already have an account? Login <Link to="/signup">here</Link></small>

                    </form> 
                </Grid>
                <Grid item sm/> 
            </Grid>
        )
    }
}

signup.propTypes = {
    classes: PropTypes.object.isRequired
}

export default withStyles(styles)(signup);
