import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import Icon from '../Images/no-img.png';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";
import { Link } from 'react-router-dom';
import CircularProgress from '@material-ui/core/CircularProgress';

const styles = {
    form: {
        textAlign: 'center'
    },
    iconImg: {
        width: 'auto',
        height: '4rem',
        margin: '20px auto 20px auto'
    },
    pageTitle: {
        margin: '10px auto 10px auto'
    },
    textField: {
        margin: '10px auto 10px auto'
    },
    button: {
        marginTop: '1rem',
    },
    customError: {
        color: 'red',
        fontSize: '0.8rem',
        marginTop: '1rem',

    }
};


class Login extends Component {
    //controled component
    constructor() {
        super();
        this.state = {
            email: '',
            password: '',
            loading: false,
            errors: {}
        };
    }

    //func to handle once user clicks submit
    handleSubmit = (event) => {
        //prevent user from submitting empty values
        event.preventDefault();
        //while everything is processing show loading
        this.setState({
            loading: true
        });

        //Get data before submitting
        const userData = {
            email: this.state.email,
            password: this.state.password
        }

        axios.post('/login', userData)
            .then(res => {
                //if successful
                console.log(res.data);
                //set loading screen to false
                this.setState({
                    loading: false
                });
                //send users to homepage
                this.props.history.push('/');
            })
            .catch(err => {
                //if errors, log error and set loading to false
                this.setState({
                    errors: err.response.data,
                    loading: false
                });
            });
    };

    //func to handleChnage in Textfields and get user input
    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    };


    render() {
        //import classes
        const { classes } = this.props;
        const { errors, loading } = this.state;

        return (
            <Grid container className={classes.form}>
                <Grid item sm/>
                <Grid item sm>
                    <img src={Icon} alt="NewUser" className={classes.iconImg}/>
                    <Typography variant="h2" className={classes.pageTitle}>Login</Typography>
                    <form noValidate onSubmit={this.handleSubmit} >
                        <TextField 
                            id="email"
                            name="email"
                            type="email"
                            label="Email"
                            className={classes.textField}
                            helperText={errors.email}
                            error={errors.email ? true : false}
                            value={this.state.email}
                            onChange={this.handleChange}
                            fullWidth 
                        />
                        <TextField 
                            id="password" 
                            name="password" 
                            type="password" 
                            label="Password" 
                            className={classes.textField}
                            helperText={errors.password}
                            error={errors.password ? true : false}
                            value={this.state.password} 
                            onChange={this.handleChange} 
                            fullWidth 
                        />
                        {errors.general && (
                            <Typography variant="h2" className={classes.customError}>
                                {errors.general}
                            </Typography>
                        )}
                        <Button 
                            type="submit" 
                            variant="contained" 
                            color="primary" 
                            className={classes.button}>

                            Login
                            {loading && (
                                <CircularProgress className={classes.progress}/>
                            )}
                        </Button>
    {/* Text that will redirect user to creat a new account(signup) */}
                            <br/><small>Dont have an account? Sign up <Link to="/signup">here</Link></small>

                    </form> 
                </Grid>
                <Grid item sm/> 
            </Grid>
        )
    }
}

Login.propTypes = {
    classes: PropTypes.object.isRequired
}

export default withStyles(styles)(Login);
